import React from 'react'
import MonitorNode from './monitor-contianer/MonitorNode';
import { useEffect, useState } from "react";
import axios from "axios";
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select, { SelectChangeEvent } from '@mui/material/Select';


export default function Monitor() {

    const [data, setData] = useState(null);
    const [state, setstate] = useState(200);
    const [age, setAge] = useState('');
    const [open, setOpen] = useState(false);

    const getNodeStatus = () => {
        axios.get('http://localhost:8080/monitor/nodes')
            .then(function (response) {
                setData(response.data);
                setstate(response.status);
                console.log(response.data);

            })
            .catch(function (error) {
                setstate(error.response.data.status);
                console.log(error);
                console.log(error.response.data.status);

            });

    }

    useEffect(() => {
        getNodeStatus();
        const interval = setInterval(
            () => { getNodeStatus(); }, 30000
        )
        return () => clearInterval(interval)
    }, []);

    const handleChange = (event) => {
        setAge(event.target.value);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleOpen = () => {
        setOpen(true);
    };


    function showData() {
        if (state === 200) {
            return (
                <div>
                    <FormControl variant="standard" sx={{ m: 1, minWidth: 100 }}>
                        <InputLabel id="demo-controlled-open-select-label">Project</InputLabel>
                        <Select
                            labelId="demo-controlled-open-select-label"
                            id="demo-controlled-open-select"
                            open={open}
                            onClose={handleClose}
                            onOpen={handleOpen}
                            value={age}
                            label="Age"
                            onChange={handleChange}
                        >
                            <MenuItem value="">
                                <em>None</em>
                            </MenuItem>
                            <MenuItem value={10} color="red">Ten</MenuItem>
                            <MenuItem value={20}>Twentyasdasdasfasfas</MenuItem>
                            <MenuItem value={30}>Thirty</MenuItem>
                        </Select>
                    </FormControl>
                    <div className='delete'>
                        <IconButton aria-label="delete" color="error">
                            <DeleteIcon />
                        </IconButton>
                    </div>

                    <div className="node-container">

                        {data?.map(node => {
                            return <MonitorNode getNodes={getNodeStatus} key={node.id} {...node}></MonitorNode>
                        })}
                    </div>
                </div>
            )
        } else if (state === 500) {
            return (
                <>

                    <h1 className='monitor-error'>No cluster found !!!</h1>

                </>
            )
        } else {
            return (
                <>
                    <h1 className='monitor-error'>Fetch failed,Resending request...</h1>
                    <p className='error-message'>error: {data}</p>
                </>
            )
        }
    }

    return (
        <>
            <div className="monitor">
                {showData()}
            </div>
        </>
    );
}
